#include <iostream>#include <vector> int main(){    int t;    std::cin >> t;    while (t > 0)    {        t--;        int n;        std::cin >> n;        std::vector<int> nums(n);        int countZero = 0;        int countNeg = 0;        for (int i = 0; i < n; i++)        {            std::cin >> nums[i];            if (nums[i] == 0)                countZero++;            if (nums[i] == -1)                countNeg++;        }         int operations = countZero;         if (countNeg % 2 != 0)        {            operations += 2;        }        std::cout << operations << std::endl;    }    return 0;}